import React, { useState, useEffect } from 'react';
import { Layout, Card, Button, Input, Modal, message,Carousel,DatePicker } from 'antd';
import TaskDetailsCarousel from './TaskDetailsCarousel';
import TaskCard from './TaskCard';

const { Header, Footer, Sider, Content } = Layout;
const TodoList = () => {
  const [todos, setTodos] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [newTaskContent, setNewTaskContent] = useState('');
  const [isSaveSuccessful, setIsSaveSuccessful] = useState(false);
  const [tasks, setTasks] = useState([]);


  useEffect(() => {
    const interval = setInterval(() => {
      // Rotate tasks every two seconds
      setCurrentIndex((prevIndex) => (prevIndex + 1) % todos.length);
    }, 2000);

    return () => clearInterval(interval);
  }, [todos]);

  const addTodo = (content, status) => {
    const newTodo = { id: Date.now(), content, status };
    setTodos([...todos, newTodo]);
  };

  const removeTodo = (todoId) => {
    setTodos(todos.filter((item) => item.id !== todoId));
  };

  const changeStatus = (todoId, newStatus) => {
    setTodos(
      todos.map((item) => (item.id === todoId ? { ...item, status: newStatus } : item))
    );
  };

  const handleSaveNewTask = () => {
    if (newTaskContent.trim() !== '') {
      addTodo(newTaskContent, 'TODO');
      setNewTaskContent('');
      setIsSaveSuccessful(true);
      setTimeout(() => {
        setIsSaveSuccessful(false);
      }, 3000);
    } else {
      message.error('Task content cannot be empty.');
    }
  };
  const showDetails = (item) => {
    Modal.info({
      title: 'Task Details',
      content: (
        <div>
          <p>Task Content: {item.content}</p>
          <p>Status: {item.status}</p>
        </div>
      ),
    });
  };
  const startTask = (task) => {
    // Logic to update task state to 'DOING'
    const updatedTasks = tasks.map((t) =>
      t.id === task.id ? { ...t, status: 'DOING' } : t
    );
    setTasks(updatedTasks);
  };

  const finishTask = (todoId) => {
    // Change the status of the task to "Completed" and move it to the "DONE" list
    changeStatus(todoId, 'DONE');
  };
  const contentStyle = {
    height: '160px',
    color: '#fff',
    lineHeight: '160px',
    textAlign: 'center',
    background: '#364d79',
  };
  return (
    <div className="todoContainer">
     <Layout>
      <Content>
        <div className="header" style={{ display: 'flex', justifyContent: 'space-between' }}>
          {/* All Tasks list */}
          <div>
            <Carousel autoplay>
              {todos.map((task) => (
                <div key={task.id} style={contentStyle}>
                  <TaskCard
                    className="task-card"
                    task={task}
                    onshowDetails={showDetails}
                    onStart={startTask}
                    onFinish={finishTask}
                  />
                </div>
              ))}
            </Carousel>
          </div>
        </div>
      </Content>
    </Layout>

      <div className="todoColumnsContainer">
        {/* All Tasks list */}
        <div className="todoColumn">

          {todos.map((item) => (
            <Card key={item.id} className="todoCard">
              {item.content}
              {/* Display "View Details" for completed tasks */}
              {item.status === 'DONE' && (
                <Button className="todoButton" onClick={() => showDetails(item)}>
                  View Details
                </Button>
              )}
              {/* Display "Click to Finish" for tasks in progress */}
              {item.status === 'DOING' && (
                <Button className="todoButton" onClick={() => finishTask(item.id)}>
                  Click to Finish
                </Button>
              )}
              <Button className="todoButton" onClick={() => removeTodo(item.id)}>
                Remove
              </Button>
            </Card>
          ))}
        </div>

        {/* New Task input */}
        <div className="todoColumn">
          <Input
            placeholder="New Task"
            value={newTaskContent}
            onChange={(e) => setNewTaskContent(e.target.value)}
          />
          <Button className="todoButton" onClick={handleSaveNewTask}>
            Save
          </Button>
          {isSaveSuccessful && <div className="successMessage">Save Successful</div>}
        </div>
      </div>
    </div>
  );
};

export default TodoList;
