import React, { useState } from 'react';
import { Card, Input, Button } from 'antd';

const TodoItem = ({ item, onRemove, onStatusChange }) => {
  return (
    <Card>
      {item.content}
      <Button onClick={() => onRemove(item.id)}>Remove</Button>
      <Button onClick={() => onStatusChange(item.id, 'DOING')}>Start</Button>
      <Button onClick={() => onStatusChange(item.id, 'DONE')}>Complete</Button>
    </Card>
  );
};

const TodoList = () => {
  const [todos, setTodos] = useState([]);

  const addTodo = content => {
    const newTodo = { id: Date.now(), content, status: 'TODO' };
    setTodos([...todos, newTodo]);
  };

  // More functions to remove and change status...

  return (
    <div>
      <Input.Search
        placeholder="Add new todo"
        enterButton="Add"
        onSearch={value => addTodo(value)}
      />
      {todos.map(item => (
        <TodoItem key={item.id} item={item} onRemove={removeTodo} onStatusChange={changeStatus} />
      ))}
    </div>
  );
};

export default TodoList;
