import React, { useState } from 'react';
import { Card, Input, Button } from 'antd';

const TodoItem = ({ item, onRemove, onStatusChange }) => {
  return (
    <Card className="todoCard">
      {item.content}
      <Button className="todoButton" onClick={() => onRemove(item.id)}>
        Remove
      </Button>
      <Button className="todoButton" onClick={() => onStatusChange(item.id, 'DOING')}>
        Start
      </Button>
      <Button className="todoButton" onClick={() => onStatusChange(item.id, 'DONE')}>
        Complete
      </Button>
    </Card>
  );
};

const TodoList = () => {
  const [todos, setTodos] = useState([]);

  const addTodo = content => {
    const newTodo = { id: Date.now(), content, status: 'TODO' };
    setTodos([...todos, newTodo]);
  };

  const removeTodo = todoId => {
    setTodos(todos.filter(item => item.id !== todoId));
  };

  const changeStatus = (todoId, newStatus) => {
    setTodos(
      todos.map(item => (item.id === todoId ? { ...item, status: newStatus } : item))
    );
  };

  return (
    <div className="todoContainer">
      <Input.Search
        className="todoInput"
        placeholder="Add new todo"
        enterButton="Add"
        onSearch={value => addTodo(value)}
      />
      <div className="todoColumnsContainer">
        {/* Assuming you have three columns: TODO, DOING, DONE */}
        {['TODO', 'DOING', 'DONE'].map(status => (
          <div key={status} className="todoColumn">
            <div className="columnHeader">{status}</div>
            {todos
              .filter(item => item.status === status)
              .map(item => (
                <TodoItem
                  key={item.id}
                  item={item}
                  onRemove={removeTodo}
                  onStatusChange={changeStatus}
                />
              ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default TodoList;
